import React, { Component } from "react";
import Navbar from "../components/layouts/navbar";
import Joi from "joi-browser";

const Input = ({ name, label, value, onChange, error }) => {
  return (
    <div className={error ? "field error" : "field"}>
      <label>{label}</label>
      <input type="text" value={value} name={name} onChange={onChange} />
      {error && (
        <div class="ui pointing red basic label">{label + " is required"}</div>
      )}
    </div>
  );
};

const InputPayment = (label, checked, value, onChange, error) => {
  return (
    <div class="field">
      <div class="ui radio checkbox">
        <input
          type="radio"
          value={value}
          checked={checked}
          onChange={onChange}
        />
        <label>{label}</label>
      </div>
      {error && (
        <div class="ui pointing red basic label">{label + " is required"}</div>
      )}
    </div>
  );
};

class Order extends Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      phoneNumber: "",
      payment: "",
      errors: {}
    };
    this.handleChange = this.handleChange.bind(this);
  }
  validate = () => {
    const errors = {};
    const state = this.state;
    if (state.firstName.trim() === "")
      errors.firstName = "Please enter the value";

    if (state.lastName.trim() === "")
      errors.lastName = "Please enter the value";

    if (state.phoneNumber.trim() === "")
      errors.phoneNumber = "Please enter the value";

    if (state.email.trim() === "") errors.email = "Please enter the value";
    if (state.payment.trim() === "") errors.email = "Please enter the value";

    return Object.keys(errors).length === 0 ? null : errors;
  };
  handleChange = e => {
    const name = e.target.name;
    const value = e.target.value;

    this.setState({ [name]: value });
  };
  handleMethodPayment = e => {
    this.setState({ payment: e.target.value });
  };
  handleSubmit = e => {
    e.preventDefault();
    const errors = this.validate();
    this.setState({ errors: errors || {} });
    if (errors) return;
  };
  render() {
    const { errors } = this.state;

    return (
      <React.Fragment>
        <Navbar />
        <div className="ui text container">
          <center>
            <h1 className="warranty">Order</h1>
            <br />
          </center>

          <form onSubmit={this.handleSubmit}>
            <div className="ui form">
              <div className="two fields">
                {/* <div className="field">
                  <label>First Name</label>
                  <input
                    type="text"
                    autoFocus={true}
                    name="firstName"
                    onChange={this.handleChange}
                  />
                </div> */}
                <Input
                  name="firstName"
                  label="First Name"
                  value={this.state.firstName}
                  onChange={this.handleChange}
                  error={errors.firstName}
                />
                <Input
                  name="lastName"
                  label="Last Name"
                  value={this.state.lastName}
                  onChange={this.handleChange}
                  error={errors.lastName}
                />
              </div>
            </div>
            <div className="ui form ">
              <div className="two fields">
                <Input
                  name="email"
                  label="Email"
                  value={this.state.email}
                  onChange={this.handleChange}
                  error={errors.email}
                />
                <Input
                  name="phoneNumber"
                  label="Phone Number"
                  value={this.state.phoneNumber}
                  onChange={this.handleChange}
                  error={errors.phoneNumber}
                />
              </div>

              <div className="field">
                <label>Payment Methods</label>
                <div class="ui form">
                  <div class="inline fields">
                    {/* <div class="field">
                      <div class="ui radio checkbox">
                        <input
                          type="radio"
                          value="ABA"
                          checked={this.state.payment === "ABA"}
                          onChange={this.handleMethodPayment}
                        />
                        <label>ABA</label>
                      </div>
                    </div> */}
                    <InputPayment
                      label="ABA"
                      checked={this.state.payment === "ABA"}
                      onChange={this.handleMethodPayment}
                      error={errors.payment}
                    />
                    <div class="field">
                      <div class="ui radio checkbox">
                        <input
                          type="radio"
                          value="WING"
                          checked={this.state.payment === "WING"}
                          onChange={this.handleMethodPayment}
                        />
                        <label>WING</label>
                      </div>
                    </div>
                    <div class="field">
                      <div class="ui radio checkbox">
                        <input
                          type="radio"
                          value="Other"
                          checked={this.state.payment === "Other"}
                          onChange={this.handleMethodPayment}
                        />
                        <label>Other</label>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
              <div className="field">
                <label>Message</label>
                <textarea />
              </div>
              <div className="field">
                <center>
                  <button className="ui ui black center button">
                    Send Message
                  </button>
                </center>
              </div>
            </div>
          </form>
        </div>
      </React.Fragment>
    );
  }
}

export default Order;
