import React, { Component } from "react";
import Navbar from "../components/layouts/navbar";
import Form from "react-validation/build/form";
import Input from "react-validation/build/input";

const required = value => {
  if (!value.toString().trim().length) {
    // We can return string or jsx as the 'error' prop for the validated Component
    return "require";
  }
};

const email = value => {
  if (!validator.isEmail(value)) {
    return `${value} is not a valid email.`;
  }
};

class Order extends Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      email: "",
      phoneNumber: "",
      payment: ""
    };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange = e => {
    const name = e.target.name;
    const value = e.target.value;
    this.setState({ [name]: value });
  };
  handleMethodPayment = e => {
    this.setState({ payment: e.target.value });
  };
  render() {
    console.log(this.state.payment);

    return (
      <React.Fragment>
        <Navbar />
        <div className="ui text container">
          <center>
            <h1 className="warranty">Order</h1>
            <br />
          </center>
          <div className="ui form">
            <div className="two fields">
              <div className="field">
                <label>First Name</label>
                <Input
                  type="text"
                  name="firstName"
                  onChange={this.handleChange}
                  validations={[required]}
                />
              </div>
              <div className="field">
                <label>Last Name</label>
                <Input
                  type="text"
                  name="lastName"
                  onChange={this.handleChange}
                  validations={[required]}
                />
              </div>
            </div>
          </div>
          <div className="ui form ">
            <div className="two fields">
              <div className="field">
                <label>email</label>
                <Input
                  type="email"
                  name="email"
                  onChange={this.handleChange}
                  validations={[required, email]}
                />
              </div>
              <div className="field">
                <label>phone number</label>
                <input
                  type="text"
                  name="phoneNumber"
                  onChange={this.handleChange}
                />
              </div>
            </div>

            <div className="field">
              <label>Payment Methods</label>
              <div class="ui form">
                <div class="inline fields">
                  <div class="field">
                    <div class="ui radio checkbox">
                      <input
                        type="radio"
                        value="ABA"
                        checked={this.state.payment === "ABA"}
                        onChange={this.handleMethodPayment}
                      />
                      <label>ABA</label>
                    </div>
                  </div>
                  <div class="field">
                    <div class="ui radio checkbox">
                      <input
                        type="radio"
                        value="WING"
                        checked={this.state.payment === "WING"}
                        onChange={this.handleMethodPayment}
                      />
                      <label>WING</label>
                    </div>
                  </div>
                  <div class="field">
                    <div class="ui radio checkbox">
                      <input
                        type="radio"
                        value="Other"
                        checked={this.state.payment === "Other"}
                        onChange={this.handleMethodPayment}
                      />
                      <label>Other</label>
                    </div>
                  </div>
                </div>
              </div>
            </div>
            <div className="field">
              <label>Message</label>
              <textarea />
            </div>
            <div className="field">
              <center>
                <button className="ui ui black center button">
                  Send Message
                </button>
              </center>
            </div>
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default Order;
