import React, { Component } from "react";
import Navbar from "../components/layouts/navbar";

class FAQ extends Component {
  question_and_answer = [
    {
      id: 1,
      question: "What?",
      answer: "Hello World",
      visible: false
    },
    {
      id: 2,
      question: "What kinds of dogs are there?",
      answer: "Hello World",
      visible: false
    },
    {
      id: 3,
      question: "How do you acquire a dog?",
      answer: "Hello World",
      visible: false
    }
  ];

  state = {
    isToggle: true
  };

  fn_toggle = () => {
    this.setState({ isToggle: !this.state.isToggle });
  };

  fn_question_and_answer = (question, answer, onClick) => {
    const { isToggle } = this.state;
    return (
      <React.Fragment>
        <div className="title" onClick={onClick}>
          {question}
        </div>
        <div className={isToggle ? "content" : "active content"}>
          <p>{answer}</p>
        </div>
      </React.Fragment>
    );
  };

  render() {
    return (
      <React.Fragment>
        <Navbar />
        <div className="ui text container">
          <center>
            <h1 className="warranty">FAQ</h1>
            <br />
          </center>
          <div className="ui accordion">
            {this.fn_question_and_answer("Hello", "Testing")}
            {this.fn_question_and_answer("Hello", "Testing")}
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default FAQ;
