import React, { Component } from "react";
import Navbar from "../components/layouts/navbar";

const ShowContent = () => {
  <React.Fragment key={key}>
    <div className="title">Test</div>
    <div className="content">
      <p>Brother</p>
    </div>
  </React.Fragment>;
};

class FAQ extends Component {
  question_and_answer = [
    {
      question: "What?",
      answer: "Hello World"
    },
    {
      question: "What kinds of dogs are there?",
      answer: "Hello World"
    },
    {
      question: "How do you acquire a dog?",
      answer: "Hello World"
    }
  ];

  constructor() {
    super();
    this.state = {
      isToggle: true
    };
  }

  fn_toggle = () => {
    this.setState({ isToggle: !this.state.isToggle });
  };

  render() {
    const { isToggle } = this.state;
    return (
      <React.Fragment>
        <Navbar />
        <div className="ui text container">
          <center>
            <h1 className="warranty">FAQ</h1>
            <br />
          </center>
          <div className="ui accordion" onClick={this.fn_toggle}>
            {this.question_and_answer.map((data, key) => {
              return (
                <React.Fragment key={key}>
                  <div className="title">{data.question}</div>
                  <div className={isToggle ? "content" : "active content"}>
                    <p>{(data.answer, key)}</p>
                  </div>
                </React.Fragment>
              );
            })}
          </div>
        </div>
      </React.Fragment>
    );
  }
}

export default FAQ;
